import React, { useState, useEffect } from 'react';  // Import useState and useEffect
import { useCart } from '../context/CartContext';  // Import the useCart hook
import axios from 'axios';

const ProductList = () => {
  const { addToCart } = useCart();  // Access the addToCart function from CartContext
  const [products, setProducts] = useState([]);  // State to hold the fetched products
  const [loading, setLoading] = useState(true);  // Loading state
  const [error, setError] = useState(null);  // State to hold errors if any

  // Fetch products from the backend when the component mounts
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/products/')
      .then(response => {
        setProducts(response.data);  // Set products from the API response
        setLoading(false);  // Set loading to false once products are fetched
      })
      .catch(err => {
        setError('Error fetching products');
        setLoading(false);  // Set loading to false on error
        console.error('Error fetching products:', err);
      });
  }, []);  // Empty dependency array ensures this effect runs once on mount

  // Show loading or error messages if necessary
  if (loading) {
    return <div>Loading...</div>;  // Show a loading message while products are being fetched
  }

  if (error) {
    return <div>{error}</div>;  // Show an error message if fetching fails
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 p-4">
      {products.map((product) => (
        <div key={product.id} className="max-w-sm rounded overflow-hidden shadow-lg bg-white">
          {/* Image with hover zoom effect */}
          <div className="relative">
            <img
              className="w-full h-64 object-cover transform transition-transform duration-300 hover:scale-110"
              src={product.image}
              alt={product.name}
            />
          </div>

          <div className="px-6 py-4">
            <h2 className="font-bold text-xl">{product.name}</h2>
            <p className="text-gray-700 text-base">{product.description}</p>
            <div className="flex justify-between items-center mt-4">
              <span className="font-bold text-xl">${product.price}</span>
              <button
                className="bg-blue-500 text-white py-2 px-4 rounded"
                onClick={() => addToCart(product)}  // Add the product to the cart
              >
                Add to Cart
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
