const CartPage = () => {
    const { cart, addToCart, removeFromCart } = useCart();
    const [quantities, setQuantities] = useState(
      cart.reduce((acc, product) => {
        acc[product.id] = 1;
        return acc;
      }, {})
    );

    const navigate = useNavigate();

    const handleProceedToCheckout = () => {
      navigate('/checkout');
    };

    const handleQuantityChange = (productId, value) => {
      setQuantities((prevQuantities) => ({
        ...prevQuantities,
        [productId]: Math.max(1, parseInt(value)),
      }));
    };

    const getTotalPrice = () => {
      return cart.reduce((total, product) => {
        const productQuantity = quantities[product.id] || 1;
        return total + (product.price * productQuantity);
      }, 0);
    };

    return (
      <div className="container mx-auto p-4">
        <h1 className="text-3xl font-semibold text-center mb-6 text-blue-600">Your Cart</h1>
        {cart.length === 0 ? (
          <p className="text-center text-lg text-gray-500">Your cart is empty!</p>
        ) : (
          <div>
            <div className="space-y-6">
              {cart.map((product) => (
                <div key={product.id} className="flex justify-between items-center p-4 border-b border-gray-200 shadow-lg rounded-md bg-white">
                  <div className="flex items-center">
                    <img src={product.image} alt={product.name} className="w-24 h-24 mr-4 rounded-md shadow-md" />
                    <div>
                      <h2 className="font-semibold text-lg text-gray-800">{product.name}</h2>
                      <p className="text-gray-600 text-sm">{product.description}</p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-4">
                    <input
                      type="number"
                      value={quantities[product.id] || 1}
                      min="1"
                      onChange={(e) => handleQuantityChange(product.id, e.target.value)}
                      className="w-16 p-2 text-center border border-gray-300 rounded-md"
                    />
                    <span className="font-semibold text-xl text-blue-600">${(product.price * (quantities[product.id] || 1)).toFixed(2)}</span>
                    <button
                      className="text-red-500 hover:text-red-700"
                      onClick={() => removeFromCart(product.id)}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
            <div className="mt-6 text-right">
              <h2 className="font-bold text-xl text-gray-800">Total: ${getTotalPrice().toFixed(2)}</h2>
              <button
                className="mt-4 bg-blue-500 text-white py-2 px-6 rounded-full hover:bg-blue-600 transition"
                onClick={handleProceedToCheckout}
              >
                Proceed to Checkout
              </button>
            </div>
          </div>
        )}
      </div>
    );
  };
