import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import axios from 'axios';
const CheckoutPage = () => {
    const { cart, getTotalPrice } = useCart();
    const [formData, setFormData] = useState({
      name: '',
      address: '',
      paymentMethod: 'creditCard',
    });
    const [isSubmitted, setIsSubmitted] = useState(false);
    const [error, setError] = useState('');
    const [quantities, setQuantities] = useState(
      cart.reduce((acc, product) => {
        acc[product.id] = 1;
        return acc;
      }, {})
    );

    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    };

    const handleSubmit = async (e) => {
      e.preventDefault();

      const token = localStorage.getItem('access_token');

      if (!token) {
        setError('Please log in to place an order');
        return;
      }

      try {
        const response = await axios.post(
          'http://127.0.0.1:8000/api/place-order/',
          {
            products: cart.map((product) => ({
              product_id: product.id,
              quantity: quantities[product.id] || 1,
            })),
            user_data: formData,
          },
          {
            headers: {
              Authorization: `Bearer ${token}`,
              'Content-Type': 'application/json',
            },
          }
        );
        setIsSubmitted(true);
      } catch (error) {
        setError('An error occurred while placing your order. Please try again.');
      }
    };

    return (
      <div className="container mx-auto p-6">
        <h1 className="text-3xl font-semibold text-center mb-6 text-blue-600">Checkout</h1>
        {isSubmitted ? (
          <div className="text-center">
            <h2 className="text-xl text-green-600">Thank you for your order!</h2>
            <p>Your order has been successfully placed.</p>
          </div>
        ) : (
          <div>
            <div className="mb-6">
              <h2 className="text-xl font-semibold">Order Summary</h2>
              <div>
                {cart.map((product) => (
                  <div key={product.id} className="flex justify-between items-center p-4 border-b border-gray-200">
                    <div>
                      <h2>{product.name}</h2>
                      <p>{product.description}</p>
                      <span>${(product.price * (quantities[product.id] || 1)).toFixed(2)}</span>
                    </div>
                  </div>
                ))}
              </div>
              <h2 className="font-bold text-xl mt-4">Total: ${getTotalPrice().toFixed(2)}</h2>
            </div>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label htmlFor="name" className="block font-medium">Full Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  className="w-full p-3 border rounded-md shadow-sm"
                />
              </div>

              <div>
                <label htmlFor="address" className="block font-medium">Shipping Address</label>
                <textarea
                  id="address"
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                  required
                  className="w-full p-3 border rounded-md shadow-sm"
                />
              </div>

              <div>
                <label htmlFor="paymentMethod" className="block font-medium">Payment Method</label>
                <select
                  id="paymentMethod"
                  name="paymentMethod"
                  value={formData.paymentMethod}
                  onChange={handleChange}
                  className="w-full p-3 border rounded-md shadow-sm"
                >
                  <option value="creditCard">Credit Card</option>
                  <option value="paypal">PayPal</option>
                </select>
              </div>

              <button type="submit" className="w-full bg-blue-500 text-white py-3 rounded-md hover:bg-blue-600">
                Place Order
              </button>
            </form>

            {error && <p className="text-red-500 text-center mt-4">{error}</p>}
          </div>
        )}
      </div>
    );
  };
