import React, { useState } from 'react';
import { useCart } from '../context/CartContext';  // Import useCart hook

const CartPage = () => {
  const { cart, addToCart, getTotalPrice } = useCart(); // Access cart, addToCart and getTotalPrice functions
  const [quantities, setQuantities] = useState(
    cart.reduce((acc, product) => {
      acc[product.id] = 1; // Default quantity is 1 for each item
      return acc;
    }, {})
  );

  // Function to update the quantity of a product
  const handleQuantityChange = (productId, newQuantity) => {
    setQuantities((prevQuantities) => ({
      ...prevQuantities,
      [productId]: newQuantity,
    }));
  };

  // Function to remove a product from the cart
  const handleRemoveFromCart = (productId) => {
    const updatedCart = cart.filter((product) => product.id !== productId);
    // Update the cart context with the filtered cart
    setQuantities((prevQuantities) => {
      const newQuantities = { ...prevQuantities };
      delete newQuantities[productId];
      return newQuantities;
    });
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl text-center mb-4">Your Cart</h1>
      {cart.length === 0 ? (
        <p className="text-center text-lg">Your cart is empty!</p>
      ) : (
        <div>
          <div className="space-y-4">
            {cart.map((product) => (
              <div key={product.id} className="flex justify-between items-center p-4 border-b">
                <div className="flex items-center">
                  <img src={product.image} alt={product.name} className="w-16 h-16 mr-4" />
                  <div>
                    <h2 className="font-semibold">{product.name}</h2>
                    <p className="text-gray-600 text-sm">{product.description}</p>
                  </div>
                </div>
                <div className="flex items-center">
                  <input
                    type="number"
                    value={quantities[product.id] || 1}
                    min="1"
                    onChange={(e) =>
                      handleQuantityChange(product.id, Math.max(1, parseInt(e.target.value)))
                    }
                    className="w-12 p-2 text-center border"
                  />
                  <span className="ml-4 font-bold text-xl">${(product.price * (quantities[product.id] || 1)).toFixed(2)}</span>
                  <button
                    className="ml-4 text-red-500"
                    onClick={() => handleRemoveFromCart(product.id)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>
          <div className="mt-6 text-right">
            <h2 className="font-bold text-xl">Total: ${getTotalPrice().toFixed(2)}</h2>
            <button className="mt-4 bg-blue-500 text-white py-2 px-6 rounded">Proceed to Checkout</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CartPage;
