import React, { useState } from 'react';
import { useCart } from '../context/CartContext';  // Import the useCart hook
import axios from 'axios';

const CheckoutPage = () => {
  const { cart, getTotalPrice, clearCart } = useCart();  // Add clearCart from context if available
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    paymentMethod: 'creditCard',
  });
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [error, setError] = useState('');
  const [quantities, setQuantities] = useState(
    cart.reduce((acc, product) => {
      acc[product.id] = 1; 
      return acc;
    }, {})
  );

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleQuantityChange = (productId, value) => {
    setQuantities((prevQuantities) => ({
      ...prevQuantities,
      [productId]: Math.max(1, parseInt(value)),
    }));
  };

  const refreshToken = async () => {
    try {
      const refreshToken = localStorage.getItem('refresh_token');

      if (!refreshToken) {
        throw new Error('No refresh token available');
      }

      const response = await axios.post('http://127.0.0.1:8000/api/refresh-token/', { refresh_token: refreshToken });
      localStorage.setItem('access_token', response.data.access_token);  // Save the new access token
      return response.data.access_token;  // Return the new token
    } catch (error) {
      console.error('Error refreshing token', error);
      setError('Session expired. Please log in again.');
      return null;  // Return null if the refresh fails
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let token = localStorage.getItem('access_token');

    if (!token) {
      setError('Please log in to place an order');
      return;
    }

    try {
      const response = await axios.post(
        'http://127.0.0.1:8000/api/place-order/', 
        {
          products: cart.map((product) => ({
            product_id: product.id,
            quantity: quantities[product.id] || 1,
          })),
          user_data: formData,  
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      console.log(response.data);
      setIsSubmitted(true);
      clearCart();  // Clear the cart after successful order placement
    } catch (error) {
      console.error('Error placing the order', error);

      // Handle token expiration
      if (error.response && error.response.data.code === 'token_not_valid') {
        setError('Your session has expired. Refreshing your token...');
        token = await refreshToken();  // Attempt to refresh the token

        if (token) {
          // Retry the order submission with the new token
          handleSubmit(e);
        } else {
          setError('Please log in again.');
        }
      } else {
        setError('An error occurred while placing your order. Please try again.');
      }
    }
  };

  // Fix for calculating the total amount
  const getTotalAmount = () => {
    return cart.reduce((total, product) => {
      const productQuantity = quantities[product.id] || 1;  // Default to 1 if quantity is undefined
      return total + (product.amount * productQuantity);  // Multiply by quantity
    }, 0);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl text-center mb-4">Checkout</h1>

      {isSubmitted ? (
        <div className="text-center">
          <h2 className="text-xl">Thank you for your order!</h2>
          <p>Your order has been successfully placed.</p>
        </div>
      ) : (
        <div>
          <div className="mb-6">
            <h2 className="text-xl">Order Summary</h2>
            <div>
              {cart.map((product) => (
                <div key={product.id} className="flex justify-between items-center p-4 border-b">
                  <div className="flex items-center">
                    {/* Display Product Image */}
                    <img 
                      src={`http://127.0.0.1:8000/api${product.image}`}  // Accessing the image as in ProductList
                      alt={product.name} 
                      className="w-16 h-16 mr-4" 
                    />
                    <div>
                      <h2>{product.name}</h2>
                      <p>{product.description}</p>
                      {/* Use 'amount' here for price */}
                      <span>${(product.amount * (quantities[product.id] || 1)).toFixed(2)}</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
            <h2 className="font-bold text-xl mt-4">Total: ${getTotalAmount().toFixed(2)}</h2> {/* Updated Total Calculation */}
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label htmlFor="name" className="block font-semibold">Full Name</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
                className="w-full p-2 border"
              />
            </div>

            <div>
              <label htmlFor="address" className="block font-semibold">Shipping Address</label>
              <textarea
                id="address"
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
                className="w-full p-2 border"
              ></textarea>
            </div>

            <div>
              <label htmlFor="paymentMethod" className="block font-semibold">Payment Method</label>
              <select
                id="paymentMethod"
                name="paymentMethod"
                value={formData.paymentMethod}
                onChange={handleChange}
                className="w-full p-2 border"
              >
                <option value="creditCard">Credit Card</option>
                <option value="paypal">PayPal</option>
              </select>
            </div>

            <button type="submit" className="bg-blue-500 text-white py-2 px-6 rounded mt-4">
              Place Order
            </button>
          </form>

          {error && <p className="text-red-500 text-center">{error}</p>}
        </div>
      )}
    </div>
  );
};

export default CheckoutPage;
