import React from 'react';
import { Link } from 'react-router-dom';

const LandingPage = () => {
  return (
    <div className="relative bg-gradient-to-r from-yellow-400 via-orange-500 to-yellow-600 h-screen flex flex-col justify-center items-center text-white">
      {/* Full Screen Background Image */}
      <div className="absolute inset-0 bg-cover bg-center opacity-50" style={{ backgroundImage: 'url(https://via.placeholder.com/1500x800)' }}></div>

      {/* Navbar */}
      <nav className="absolute top-0 left-0 w-full p-4 bg-transparent flex justify-between items-center">
        <div className="text-white text-2xl font-semibold">
          <span>AYO</span>
        </div>
        <div className="space-x-8">
          <Link to="/why" className="text-white hover:text-yellow-400">WHY AYO</Link>
          <Link to="/science" className="text-white hover:text-yellow-400">OUR SCIENCE</Link>
          <Link to="/success-stories" className="text-white hover:text-yellow-400">SUCCESS STORIES</Link>
          <Link to="/faq" className="text-white hover:text-yellow-400">FAQ</Link>
        </div>
        <div className="flex items-center space-x-4">
          <Link to="/cart" className="text-white hover:text-yellow-400">CART</Link>
          <Link to="/buy" className="bg-black text-yellow-400 py-2 px-6 rounded-full hover:bg-yellow-400 hover:text-black transition">BUY AYO</Link>
        </div>
      </nav>

      {/* Main Content */}
      <div className="relative z-10 text-center px-4 md:px-16">
        <h1 className="text-5xl md:text-7xl font-extrabold leading-tight mb-4">
          World's First Circadian Health Wearable
        </h1>
        <p className="text-lg md:text-2xl mb-8">
          Sleep Better. Boost Energy. Embrace Wellness.
        </p>

        {/* Call-to-Action Button */}
        <div className="flex justify-center gap-8">
          <Link to="/shop" className="bg-black text-yellow-400 py-3 px-6 rounded-full text-xl transition duration-300 hover:bg-yellow-400 hover:text-black">
            SHOP NOW
          </Link>
        </div>
      </div>

      {/* Optional Product Image */}
      <div className="absolute bottom-20 right-16 md:bottom-32 md:right-32">
        <img src="https://via.placeholder.com/200x400" alt="Product" className="rounded-lg shadow-lg" />
      </div>
    </div>
  );
};

export default LandingPage;
